import React, { useEffect, useState } from 'react';
import { ethers } from 'ethers';
import { BrowserRouter as Router, Route} from 'react-router-dom';
import Navigation from './components/Navigation';
import Section from './components/Section';
import Product from './components/Product';
import Transaction from './components/transaction.js'; // Correct import path
import withAccount from './components/withAccount'; // Correct import path
import Dappazon from './abis/Dappazon.json';
import config from './config.json';

function App() {
  const [provider, setProvider] = useState(null);
  const [dappazon, setDappazon] = useState(null);
  const [account, setAccount] = useState(null);
  const [electronics, setElectronics] = useState(null);
  const [clothing, setClothing] = useState(null);
  const [toys, setToys] = useState(null);
  const [item, setItem] = useState({});
  const [toggle, setToggle] = useState(false);

  const togglePop = (item) => {
    setItem(item);
    setToggle(!toggle); // Corrected toggle logic
  };

  const loadBlockchainData = async () => {
    try {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      setProvider(provider);
      const network = await provider.getNetwork();
      const dappazon = new ethers.Contract(config[network.chainId].dappazon.address, Dappazon, provider);
      setDappazon(dappazon);
      const items = [];

      for (let i = 0; i < 9; i++) {
        const item = await dappazon.items(i + 1);
        items.push(item);
      }

      const electronics = items.filter((item) => item.category === 'electronics');
      const clothing = items.filter((item) => item.category === 'clothing');
      const toys = items.filter((item) => item.category === 'toys');

      setElectronics(electronics);
      setClothing(clothing);
      setToys(toys);
    } catch (error) {
      console.error('Error loading blockchain data:', error);
    }
  };

  useEffect(() => {
    loadBlockchainData();
  }, []);

  return (
    <div>
      <Navigation account={account} setAccount={setAccount} />
      <h2 style={{ textAlign: 'center' }}>Welcome to PranaSwap!</h2>
      {electronics && clothing && toys && (
        <>
          <Section title="Clothing & Jewelry" items={clothing} togglePop={togglePop} />
          <Section title="Electronics & Gadgets" items={electronics} togglePop={togglePop} />
          <Section title="Toys & Gaming" items={toys} togglePop={togglePop} />
        </>
      )}
      {toggle && (
        <Product item={item} provider={provider} account={account} dappazon={dappazon} togglePop={togglePop} />
      )}
    </div>
  );
}

// Wrap Transaction component with the withAccount HOC and provide routing
const AppWithRouter = () => {
  const TransactionWithAccount = withAccount(Transaction);
  return (
    <Router>
      <Switch>
        <Route exact path="/">
          <App />
        </Route>
        <Route path="/transactions">
          <TransactionWithAccount />
        </Route>
      </Switch>
    </Router>
  );
};

export default AppWithRouter;
