import { ethers } from 'ethers';
import { useState, useEffect } from 'react';
import './Navigation.css';

const Navigation = ({ account, setAccount }) => {
    const [balance, setBalance] = useState(0);

    useEffect(() => {
        const fetchBalance = async () => {
            if (account) {
                const provider = new ethers.providers.Web3Provider(window.ethereum);
                const balance = await provider.getBalance(account);
                setBalance(ethers.utils.formatEther(balance));

                // Subscribe to account balance changes
                provider.on(account, async (newBalance) => {
                    setBalance(ethers.utils.formatEther(newBalance));
                });
            }
        };

        fetchBalance();

        return () => {
            // Cleanup: unsubscribe from balance changes when component unmounts
            if (account) {
                const provider = new ethers.providers.Web3Provider(window.ethereum);
                provider.removeAllListeners(account);
            }
        };
    }, [account]);

    const connectHandler = async () => {
        const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
        const account = ethers.utils.getAddress(accounts[0]);
        setAccount(account);
    };

    return (
        <nav>
            <div className='nav__brand'>
                <img src="/pranaswap_logo.jpeg" alt="PranaSwap Logo" className="logo" />
                <h1>PranaSwap</h1>
            </div>

            <div className="account-info">
                {account ? (
                    <button type="button" className='nav__connect' onClick={connectHandler}>
                        {`${account.slice(0, 6)}... | bal: ${balance.slice(0, 6)}..`}
                    </button>
                ) : (
                    <button type="button" className='nav__connect' onClick={connectHandler}>
                        Connect
                    </button>
                )}
            </div>

            <ul className='nav__links'>
                <li><a href="#Clothing & Jewelry">Renewable Energy</a></li>
                <li><a href="#Electronics & Gadgets">Fossil Fuels</a></li>
                <li><a href="#Toys & Gaming">Nuclear Energy</a></li>
            </ul>
        </nav>
    );
};

export default Navigation;
